                '结算转余额'
             WHEN o.finance_type = 14
             THEN
                '结算后退货存款'
             WHEN o.finance_type = 15
             THEN
                'Paypal支付'
             WHEN o.finance_type = 16
             THEN
                '保管费'
             WHEN o.finance_type = 17
             THEN
                '保险费'
