CREATE OR REPLACE PROCEDURE "ZHAO"."P_BID_AUCTION"
(
	p_user_id       NUMBER,
	p_user_nickname VARCHAR2,
	p_auction_id    NUMBER,
	p_price         IN OUT NUMBER,
	p_initial_price IN OUT NUMBER,
	p_ip            VARCHAR,
	p_auction_no    NUMBER,
	p_auction_name  VARCHAR,
	p_app_id        VARCHAR,
	RESULT_STATUS   OUT NUMBER
	/*
                  作者： Dwen
                  日期： 2011-07-22
                  描述： 竞拍出价
                  0: 出价成功
                  1: 用户不存在
                  2: 用户锁定冻结
                  3: 用户额度不够
                  4: 拍品已结标或过期
                  5: 加价幅度不符合
                  6: 不可重复出价
                  7: 系统锁错误
                  8: 系统末知错误
              */
	/*
              更新:2012-05-12.李征
              拍品第一口价大于初始价时,自动出代理价
              P_BID_PRIMITIVE添加参数p_price
         */
) IS
	v_now              DATE := SYSDATE;
	v_current_price    NUMBER := 0;
	v_last_user_id     NUMBER := 0;
	v_last_proxy_price NUMBER := 0;
	v_last_ip          VARCHAR(200);
	v_next_legal_price NUMBER := 0;
	v_price_interval   NUMBER := 0;
	v_auction_lock     NUMBER;
	v_isauction        CHAR(1) := '1';
	v_last_app_id      VARCHAR(255);
BEGIN
	/*========================竞拍出价=========================*/
	/*The price should be times of v_price_interval*/
	BEGIN
		P_BID_INTERVAL(p_price, v_next_legal_price, v_price_interval);
	END;
	IF MOD(p_price, v_price_interval) > 0 THEN
		BEGIN
			p_price := p_price - MOD(p_price, v_price_interval);
		END;
	END IF;

	/* 对当前拍品记录进行锁定 */
	BEGIN
		SELECT ID
			INTO v_auction_lock
			FROM AUCTION_MAX_PRICES_NEW
		 WHERE AUCTION_ID = p_auction_id
			 AND ROWNUM = 1
			 FOR UPDATE;
	EXCEPTION
		WHEN NO_DATA_FOUND THEN
			ROLLBACK;
			RESULT_STATUS := 7;
			RETURN;
	END;

	BEGIN
		/* 查询当前拍品最大价格记录 */
		SELECT MAX_PRICE, USER_ID, PROXY_PRICE, APP_ID
			INTO v_current_price,
					 v_last_user_id,
					 v_last_proxy_price,
					 v_last_app_id
			FROM AUCTION_MAX_PRICES_NEW
		 WHERE AUCTION_ID = p_auction_id
			 AND ROWNUM = 1;
	EXCEPTION
		WHEN NO_DATA_FOUND THEN
			ROLLBACK;
			RESULT_STATUS := 8;
			RETURN;
	END;

	BEGIN
		/* 查询拍品出价记录IP*/
		SELECT IP
			INTO v_last_ip
			FROM AUCTION_PRICES
		 WHERE AUCTION_ID = p_auction_id
			 AND IS_MAX = 1
			 AND ROWNUM = 1;
	EXCEPTION
		WHEN NO_DATA_FOUND THEN
			RESULT_STATUS := 0;
	END;

	IF v_last_user_id IS NULL THEN
		/* 该拍品末出过价情况 */
		P_BID_PRIMITIVE(p_user_id,
										p_user_nickname,
										p_auction_id,
										p_price,
										p_initial_price,
										p_ip,
										p_auction_no,
										p_auction_name,
										p_app_id,
										RESULT_STATUS);
	ELSE
		/* 该拍品出过价情况 */
		P_BID_HAD_PRICE(p_user_id,
										p_user_nickname,
										p_auction_id,
										p_price,
										v_current_price,
										v_last_user_id,
										v_last_proxy_price,
										p_ip,
										v_last_ip,
										p_auction_no,
										p_auction_name,
										p_app_id,
										v_last_app_id,
										RESULT_STATUS);
	END IF;
END P_BID_AUCTION;

 
/

