                     RESULT_STATUS如下：
                     0: 出价成功
                     1: 用户不存在
                     2: 用户锁定冻结
                     3: 用户额度不够
                     4: 拍品已结标或过期
                     5: 加价幅度不符合
                     6: 不可重复出价
                     7: 系统锁错误
                     8: 系统末知错误
                */
) IS
	v_now           DATE := SYSDATE;
	v_current_price NUMBER := 0;
	v_last_user_id  NUMBER := 0;
	v_auction_lock  NUMBER;
BEGIN

	/* 对当前拍品记录进行锁定 */
	BEGIN
		SELECT ID
			INTO v_auction_lock
			FROM AUCTION_MAX_PRICES_NEW
		 WHERE AUCTION_ID = p_auction_id
			 AND ROWNUM = 1
			 FOR UPDATE;
	EXCEPTION
		WHEN NO_DATA_FOUND THEN
			ROLLBACK;
			RESULT_STATUS := 7;
			RETURN;
	END;

	BEGIN
		/* 更改当前一口价商品截止时间为当前时间 */
		UPDATE AUCTIONS SET END_AT = SYSDATE WHERE ID = p_auction_id;
		/* 查询当前拍品最大价格记录 */
		SELECT MAX_PRICE, USER_ID
			INTO v_current_price, v_last_user_id
			FROM AUCTION_MAX_PRICES_NEW
		 WHERE AUCTION_ID = p_auction_id
			 AND ROWNUM = 1;
	EXCEPTION
		WHEN NO_DATA_FOUND THEN
			/* 末找到数据 */
			ROLLBACK;
			RESULT_STATUS := 8;
			RETURN;
		WHEN OTHERS THEN
			/* 其它异常 */
			ROLLBACK;
			RESULT_STATUS := 8;
			RETURN;
	END;

	/* 当前出价不能小于拍品最大记录价格 */
	IF p_price < v_current_price THEN
		BEGIN
			ROLLBACK;
			RESULT_STATUS := 5;
			RETURN;
		END;
	END IF;

	/* 一口价拍品中最大价格记录表中确保无用户出过价*/
	IF v_last_user_id IS NULL THEN
		BEGIN
			/* 减去用户额度 */
			UPDATE USERS SET QUOTA = QUOTA - p_price WHERE id = p_user_id;
		
			/* 记录用户出价记录*/
			INSERT INTO AUCTION_PRICES
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 NICKNAME,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_id,
				 p_user_nickname,
				 p_price,
				 p_ip,
				 0,
				 v_now,
				 1,
				 p_app_id);
		
			/* 更改拍品最大记录*/
			UPDATE AUCTION_MAX_PRICES_NEW
				 SET USER_ID      = p_user_id,
						 NICKNAME     = p_user_nickname,
						 AUCTION_TIME = 1,
						 MAX_PRICE    = p_price,
						 PROXY_PRICE  = 0.0,
						 APP_ID       = p_app_id
			 WHERE AUCTION_ID = p_auction_id;
		
			/*记录用户额度占用*/
			INSERT INTO USER_QUOTA_USAGES
				(ID,
				 USER_ID,
				 AUCTION_ID,
				 AUCTION_NO,
				 AUCTION_NAME,
				 USE_QUOTA,
				 STATUS,
				 USE_TYPE,
				 CREATED_AT)
			VALUES
				(USER_QUOTA_USAGES_SEQ.NEXTVAL,
				 p_user_id,
				 p_auction_id,
				 p_auction_no,
				 p_auction_name,
				 p_price,
				 1,
				 0,
				 v_now);
		
		EXCEPTION
			WHEN DUP_VAL_ON_INDEX THEN
				/*插入数据唯一索引对应的列上有重复值*/
				ROLLBACK;
				RESULT_STATUS := 8;
				RETURN;
			WHEN NO_DATA_FOUND THEN
				/* 末找到数据 */
				ROLLBACK;
				RESULT_STATUS := 8;
				RETURN;
			WHEN OTHERS THEN
				/* 其它异常 */
				ROLLBACK;
				RESULT_STATUS := 8;
				RETURN;
		END;
	END IF;
	--一口价出价后直接生成交易单
	p_user_make_one_user_orders(p_user_id, RESULT_STATUS);

	RESULT_STATUS := 0;
END P_BID_BUY_NOW;

 
/

-- ----------------------------
-- Procedure structure for P_BID_CANCEL_PROXY
-- ----------------------------
CREATE OR REPLACE PROCEDURE "ZHAO"."P_BID_CANCEL_PROXY"
(
	p_user_id      IN NUMBER,
	p_auction_id   IN NUMBER,
	p_max_price    OUT NUMBER,
	p_last_user_id OUT NUMBER,
	p_bid_times    OUT NUMBER,
	P_USER_QUOTA   OUT NUMBER,
	RESULT_STATUS  OUT NUMBER
	/*
              作者：李征
              日期：2012.05.09
              描述：取消代理价,返回当前用户及当前价
              RESULT_STATUS说明：
              10：表示取消代理价成功.
              11：表示末找到当前拍品的额度占用记录.
              12：表示取消代理价失败.
           */
) IS

	v_current_price    NUMBER := 0;
	v_last_user_id     NUMBER := 0;
	v_last_proxy_price NUMBER := 0;
	v_bid_times        NUMBER := 0;
	v_user_quota       NUMBER := 0;

	v_now DATE := SYSDATE;
BEGIN
	RESULT_STATUS := 0;

	/* 查询当前拍品最大记录 */
	BEGIN
		SELECT MAX_PRICE, USER_ID, PROXY_PRICE, AUCTION_TIME, U.QUOTA
			INTO v_current_price,
					 v_last_user_id,
					 v_last_proxy_price,
					 v_bid_times,
					 v_user_quota
			FROM AUCTION_MAX_PRICES_NEW AMP
			JOIN USERS U ON U.ID = AMP.USER_ID
		 WHERE AUCTION_ID = p_auction_id
			 AND ROWNUM = 1;
	EXCEPTION
		WHEN NO_DATA_FOUND THEN
			ROLLBACK;
			RESULT_STATUS := 11;
			RETURN;
	END;

	/* 拍品最大记录用户是当前用户情况 */
	IF v_last_user_id = p_user_id AND v_last_proxy_price > 0 THEN
		BEGIN
			/* 额度释放 */
			UPDATE USERS
				 SET QUOTA = QUOTA + (v_last_proxy_price - v_current_price)
			 WHERE ID = p_user_id;
		
			/* 更改额度占用状态为不占用 */
			UPDATE USER_QUOTA_USAGES
				 SET STATUS = 0
			 WHERE USER_ID = p_user_id
				 AND AUCTION_ID = p_auction_id;
		
			/* 更改代理价为0 */
			UPDATE AUCTION_MAX_PRICES_NEW
				 SET PROXY_PRICE = 0
			 WHERE AUCTION_ID = p_auction_id;
		
			/* 插入额度占用 */
			INSERT INTO USER_QUOTA_USAGES
				(ID, USER_ID, AUCTION_ID, USE_QUOTA, STATUS, USE_TYPE, CREATED_AT)
			VALUES
				(USER_QUOTA_USAGES_SEQ.NEXTVAL,
				 p_user_id,
				 p_auction_id,
				 v_current_price,
				 1,
				 0,
				 v_now);
		
			UPDATE AUCTION_PROXY_PRICES AP
				 SET AP.STATUS = 0, AP.UPDATED_AT = SYSDATE
			 WHERE AUCTION_ID = p_auction_id
				 AND USER_ID = p_user_id;
		
		EXCEPTION
			WHEN DUP_VAL_ON_INDEX THEN
				/*插入数据唯一索引对应的列上有重复值*/
				ROLLBACK;
				RESULT_STATUS := 12;
			
			WHEN NO_DATA_FOUND THEN
				/* 末找到数据 */
				ROLLBACK;
				RESULT_STATUS := 13;
			
			WHEN OTHERS THEN
				/* 其它异常 */
				ROLLBACK;
				RESULT_STATUS := 14;
			
		END;
	END IF;
	IF result_status != 0 THEN
		p_max_price    := 0;
		p_last_user_id := 0;
		p_bid_times    := 0;
		P_USER_QUOTA   := 0;
		RETURN;
	END IF;
	P_USER_QUOTA   := v_user_quota + (v_last_proxy_price - v_current_price);
	p_last_user_id := v_last_user_id;
	p_max_price    := v_current_price;
	p_bid_times    := v_bid_times;
	RESULT_STATUS  := 10;
END P_BID_CANCEL_PROXY;

 
/

-- ----------------------------
-- Procedure structure for P_BID_HAD_PRICE
-- ----------------------------
CREATE OR REPLACE PROCEDURE "ZHAO"."P_BID_HAD_PRICE" (P_USER_ID          NUMBER,
                                            P_USER_NICKNAME    VARCHAR2,
                                            P_AUCTION_ID       NUMBER,
                                            P_PRICE            IN OUT NUMBER,
                                            P_CURRENT_PRICE    NUMBER,
                                            P_LAST_USER_ID     NUMBER,
                                            P_LAST_PROXY_PRICE NUMBER,
                                            P_IP               VARCHAR,
                                            P_LAST_IP          VARCHAR,
                                            P_AUCTION_NO       NUMBER,
                                            P_AUCTION_NAME     VARCHAR,
                                            P_APP_ID           VARCHAR,
                                            P_LAST_APP_ID      VARCHAR,
                                            RESULT_STATUS      OUT NUMBER
                                            /*
                                                      作者：  Dwen
                                                      日期：  2011-07-22
                                                      描述：  当前拍品出过价情况
                                                      RESULT_STATUS说明：
                                                      0: 出价成功
                                                      1: 用户不存在
                                                      2: 用户锁定冻结
                                                      3: 用户额度不够
                                                      4: 拍品已结标或过期
                                                      5: 加价幅度不符合
                                                      6: 不可重复出价
                                                      7: 系统锁错误
                                                      8: 系统末知错误

                                                      修改日期:2012.11.23
                                                      修改人:李征
                                                      描述:更新代理价时,记录新代理价
                                                  */) IS
  V_NEXT_LEGAL_PRICE NUMBER := 0;
  V_PRICE_INTERVAL   NUMBER := 0;
BEGIN
  /*===============当前拍品出过价情况====================*/

  /* 调制拍品出价价格 */
  BEGIN
    P_BID_INTERVAL(P_CURRENT_PRICE, V_NEXT_LEGAL_PRICE, V_PRICE_INTERVAL);
  END;
  /* 检查用户是否重复出价 */
  IF P_LAST_USER_ID = P_USER_ID THEN
    BEGIN
      P_BID_MODIFY_PROXY_2(P_USER_ID,
                         P_USER_NICKNAME,
                         P_AUCTION_ID,
                         P_PRICE,
                         P_APP_ID,
                         RESULT_STATUS);
      IF RESULT_STATUS != 0 THEN
        ROLLBACK;
        RESULT_STATUS := 6;
        RETURN;
      END IF;
      /*自动设置代理价*/
      RESULT_STATUS :=0;
      RETURN;
    END;
  END IF;

  /* 当前出价不能小于拍品下一个出价 */
  IF P_PRICE < V_NEXT_LEGAL_PRICE THEN
    BEGIN
      ROLLBACK;
      RESULT_STATUS := 5;
      RETURN;
    END;
  END IF;

  BEGIN
    /*  更新当前拍品最高出价状态为'否'*/
    UPDATE AUCTION_PRICES
       SET IS_MAX = 0
     WHERE AUCTION_ID = P_AUCTION_ID
       AND IS_MAX = 1;
  EXCEPTION
    WHEN OTHERS THEN
      /* 其它异常 */
      ROLLBACK;
      RESULT_STATUS := 8;
      RETURN;
  END;

  IF P_LAST_PROXY_PRICE > 0 THEN
    /* 当前拍品有代理价 */
    P_BID_HAD_PROXY(P_USER_ID,
                    P_USER_NICKNAME,
                    P_AUCTION_ID,
                    P_PRICE,
                    P_LAST_USER_ID,
                    P_LAST_PROXY_PRICE,
                    P_IP,
                    P_LAST_IP,
                    P_APP_ID,
                    P_LAST_APP_ID,
                    P_AUCTION_NO,
                    P_AUCTION_NAME,
                    RESULT_STATUS);
  ELSE
    /* 当前拍品无代理价 */
    P_BID_NONE_PROXY(P_USER_ID,
                     P_USER_NICKNAME,
                     P_AUCTION_ID,
                     P_PRICE,
                     V_NEXT_LEGAL_PRICE,
                     P_LAST_USER_ID,
                     P_IP,
                     P_APP_ID,
                     P_LAST_USER_ID,
                     P_AUCTION_NO,
                     P_AUCTION_NAME,
                     RESULT_STATUS);
  END IF;

END P_BID_HAD_PRICE;


 
/

-- ----------------------------
-- Procedure structure for P_BID_HAD_PROXY
-- ----------------------------
CREATE OR REPLACE PROCEDURE "ZHAO"."P_BID_HAD_PROXY"
(
	p_user_id          NUMBER,
	p_user_nickname    VARCHAR2,
	p_auction_id       NUMBER,
	p_price            IN OUT NUMBER,
	p_last_user_id     NUMBER,
	p_last_proxy_price NUMBER,
	p_ip               VARCHAR,
	p_last_ip          VARCHAR,
	p_app_id           VARCHAR,
	p_last_app_id      VARCHAR,
	p_auction_no       NUMBER,
	p_auction_name     VARCHAR,
	RESULT_STATUS      OUT NUMBER
	/*
                            作者： Dwen
                            日期： 2011-07-22
                            描述：当前拍品有代理价情况
                            RESULT_STATUS说明：
                            0: 出价成功
                            1: 用户不存在
                            2: 用户锁定冻结
                            3: 用户额度不够
                            4: 拍品已结标或过期
                            5: 加价幅度不符合
                            6: 不可重复出价
                            7: 系统锁错误
                            8: 系统末知错误
                        */
) IS
	v_now                DATE := SYSDATE;
	v_next_legal_price   NUMBER := 0;
	v_price_interval     NUMBER := 0;
	v_used_quota         NUMBER := 0;
	v_last_user_nickname VARCHAR2(255);
BEGIN
	SELECT nickname
		INTO v_last_user_nickname
		FROM auction_max_prices_new
	 WHERE user_id = p_last_user_id
		 AND auction_id = p_auction_id;

	/*===================当前拍品有代理价情况====================*/
	/*----------代理价大于等于当前出价--------------*/
	IF p_last_proxy_price >= p_price THEN
		BEGIN
			/* 记录(当前)用户出价记录(当前用户记录出局) */
			INSERT INTO AUCTION_PRICES
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_id,
				 p_user_nickname,
				 p_price,
				 p_ip,
				 0,
				 v_now,
				 0,
				 p_app_id);
		
			INSERT INTO AUCTION_PRICES_PARTITION
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_id,
				 p_user_nickname,
				 p_price,
				 p_ip,
				 0,
				 v_now,
				 0,
				 p_app_id);
		
			/* 记录(有代理价)用户出价记录 */
			INSERT INTO AUCTION_PRICES
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_last_user_id,
				 v_last_user_nickname,
				 p_price,
				 p_last_ip,
				 1,
				 v_now,
				 1,
				 p_last_app_id);
			INSERT INTO AUCTION_PRICES_PARTITION
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_last_user_id,
				 v_last_user_nickname,
				 p_price,
				 p_last_ip,
				 1,
				 v_now,
				 1,
				 p_last_app_id);
		
			/*-------当前出价等于当前拍品代理价-------*/
			IF p_price = p_last_proxy_price THEN
				/* 更新当前拍品代理价为0,最大价格为当前价*/
				UPDATE AUCTION_MAX_PRICES_NEW
					 SET MAX_PRICE    = p_price,
							 PROXY_PRICE  = 0,
							 AUCTION_TIME = AUCTION_TIME + 2,
							 APP_ID       = p_last_app_id
				 WHERE AUCTION_ID = p_auction_id;
				/*------当前拍品代理价大于当前出价--------*/
			ELSE
				/* 更新最大价格为当前价*/
				UPDATE AUCTION_MAX_PRICES_NEW
					 SET MAX_PRICE    = p_price,
							 AUCTION_TIME = AUCTION_TIME + 2,
							 APP_ID       = p_last_app_id
				 WHERE AUCTION_ID = p_auction_id;
			END IF;
		
			/*出价用户额度占用 */
			INSERT INTO USER_QUOTA_USAGES
				(ID,
				 USER_ID,
				 AUCTION_ID,
				 AUCTION_NO,
				 AUCTION_NAME,
				 USE_QUOTA,
				 STATUS,
				 USE_TYPE,
				 CREATED_AT)
			VALUES
				(USER_QUOTA_USAGES_SEQ.NEXTVAL,
				 p_user_id,
				 p_auction_id,
				 p_auction_no,
				 p_auction_name,
				 p_price,
				 0,
				 0,
				 v_now);
		EXCEPTION
			WHEN DUP_VAL_ON_INDEX THEN
				/*插入数据唯一索引对应的列上有重复值*/
				ROLLBACK;
				RESULT_STATUS := 8;
				RETURN;
			WHEN NO_DATA_FOUND THEN
				/* 末找到数据 */
				ROLLBACK;
				RESULT_STATUS := 8;
				RETURN;
			WHEN OTHERS THEN
				/* 其它异常 */
				ROLLBACK;
				RESULT_STATUS := 8;
				RETURN;
		END;
		/*-------------当前出价大于代理价----------------*/
	ELSE
		BEGIN
			/*====START_释放当前拍品有代理价用户的额度====*/
			/* 统计所占当前拍品的额度 */
			SELECT nvl(SUM(USE_QUOTA), 0)
				INTO v_used_quota
				FROM USER_QUOTA_USAGES
			 WHERE USER_ID = p_last_user_id
				 AND AUCTION_ID = p_auction_id
				 AND STATUS = 1;
			/* 回滚该用户当前拍品所占额度 */
			UPDATE USERS
				 SET QUOTA = QUOTA + v_used_quota
			 WHERE ID = p_last_user_id;
			/* 该用户对当前拍品额度状态更改成不占用 */
			UPDATE USER_QUOTA_USAGES
				 SET STATUS = 0
			 WHERE USER_ID = p_last_user_id
				 AND AUCTION_ID = p_auction_id;
			/*====END_释放当前拍品有代理价用户的额度====*/
		
			/* 记录当前用户额度占用 */
			INSERT INTO USER_QUOTA_USAGES
				(ID,
				 USER_ID,
				 AUCTION_ID,
				 AUCTION_NO,
				 AUCTION_NAME,
				 USE_QUOTA,
				 STATUS,
				 USE_TYPE,
				 CREATED_AT)
			VALUES
				(USER_QUOTA_USAGES_SEQ.NEXTVAL,
				 p_user_id,
				 p_auction_id,
				 p_auction_no,
				 p_auction_name,
				 p_price,
				 1,
				 1,
				 v_now);
			/* 减去用户出价所占额度 */
			UPDATE USERS SET QUOTA = QUOTA - p_price WHERE id = p_user_id;
		
			/* 记录(当前)用户出价记录 */
			INSERT INTO AUCTION_PRICES
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_id,
				 p_user_nickname,
				 p_last_proxy_price,
				 p_ip,
				 1,
				 v_now,
				 0,
				 p_app_id);
			INSERT INTO AUCTION_PRICES_PARTITION
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_id,
				 p_user_nickname,
				 p_last_proxy_price,
				 p_ip,
				 1,
				 v_now,
				 0,
				 p_app_id);
		
			/* 记录(代理价)用户出价记录 */
			INSERT INTO AUCTION_PRICES
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_last_user_id,
				 v_last_user_nickname,
				 p_last_proxy_price,
				 p_last_ip,
				 1,
				 v_now,
				 0,
				 p_last_app_id);
		
			INSERT INTO AUCTION_PRICES_PARTITION
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_last_user_id,
				 v_last_user_nickname,
				 p_last_proxy_price,
				 p_last_ip,
				 1,
				 v_now,
				 0,
				 p_last_app_id);
			/*
      * 根据业务规则，不能把当前出价显示为代理价,
      * 因为当前出价大于代理价已成为前拍品代理价。必须对当前拍品竞拍价格进行调制。
      */
			BEGIN
				P_BID_INTERVAL(p_last_proxy_price,
											 v_next_legal_price,
											 v_price_interval);
			END;
		
			/* 记录(当前)用户出价记录 */
			INSERT INTO AUCTION_PRICES
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_id,
				 p_user_nickname,
				 v_next_legal_price,
				 p_ip,
				 1,
				 v_now,
				 1,
				 p_app_id);
			INSERT INTO AUCTION_PRICES_PARTITION
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_id,
				 p_user_nickname,
				 v_next_legal_price,
				 p_ip,
				 1,
				 v_now,
				 1,
				 p_app_id);
			/*------当前出价大于该拍品下一个合法价------ */
			IF p_price > v_next_legal_price THEN
				/* 更改该拍品最大价格为当前用户，下一个合法价为最大价，当前出价为代理价*/
				UPDATE AUCTION_MAX_PRICES_NEW
					 SET USER_ID      = p_user_id,
							 nickname     = p_user_nickname,
							 MAX_PRICE    = v_next_legal_price,
							 PROXY_PRICE  = p_price,
							 AUCTION_TIME = AUCTION_TIME + 3,
							 APP_ID       = p_app_id
				 WHERE AUCTION_ID = p_auction_id;
			
				/* 记录代理价出价记录 */
				INSERT INTO AUCTION_PROXY_PRICES
					(ID,
					 AUCTION_ID,
					 PROXY_PRICE,
					 USER_ID,
					 nickname,
					 CREATED_AT,
					 STATUS,
					 APP_ID)
				VALUES
					(AUCTION_PROXY_PRICES_SEQ.NEXTVAL,
					 p_auction_id,
					 p_price,
					 p_user_id,
					 p_user_nickname,
					 v_now,
					 1,
					 p_app_id);
				/*-------当前出价小于或等于该拍品下一个合法价-------*/
			ELSE
				/* 更改该拍品最大价格为当前用户，下一个合法价为最大价，代理价为0*/
				UPDATE AUCTION_MAX_PRICES_NEW
					 SET USER_ID      = p_user_id,
							 nickname     = p_user_nickname,
							 MAX_PRICE    = v_next_legal_price,
							 PROXY_PRICE  = 0,
							 AUCTION_TIME = AUCTION_TIME + 3,
							 APP_ID       = p_app_id
				 WHERE AUCTION_ID = p_auction_id;
			END IF;
		EXCEPTION
			WHEN DUP_VAL_ON_INDEX THEN
				/*插入数据唯一索引对应的列上有重复值*/
				ROLLBACK;
				RESULT_STATUS := 8;
				RETURN;
			WHEN NO_DATA_FOUND THEN
				/* 末找到数据 */
				ROLLBACK;
				RESULT_STATUS := 8;
				RETURN;
			WHEN OTHERS THEN
				/* 其它异常 */
				ROLLBACK;
				RESULT_STATUS := 8;
				RETURN;
		END;
	END IF;
	RESULT_STATUS := 0;
END P_BID_HAD_PROXY;

 
