	EXCEPTION
	WHEN NO_DATA_FOUND THEN
		ROLLBACK;
		RESULT_STATUS:=1;
		RETURN;
	END;

	/*  检查用户是否被锁 */
	IF v_islock = 1 THEN
		BEGIN
