		END;
	END IF;

	/* 检查当前拍品是否可以合法出价 */
	BEGIN
		SELECT INITIAL_PRICE INTO v_initial_price FROM AUCTIONS WHERE END_AT>=v_now AND START_AT<=v_now AND STATUS=2 AND ID=p_auction_id;
