	/* 获得当前拍最大价格记录 */
	BEGIN
		SELECT MAX_PRICE,USER_ID,PROXY_PRICE INTO v_current_price, v_last_user_id,v_last_proxy_price FROM AUCTION_MAX_PRICES_NEW WHERE AUCTION_ID=p_auction_id AND USER_ID=p_user_id;
