CREATE OR REPLACE PROCEDURE "ZHAO"."P_BID_NONE_PROXY"
(
	p_user_id          NUMBER,
	p_user_nickname    VARCHAR2,
	p_auction_id       NUMBER,
	p_price            IN OUT NUMBER,
	p_next_legal_price NUMBER,
	p_last_user_id     NUMBER,
	p_ip               VARCHAR,
	p_app_id           VARCHAR,
	p_last_app_id      VARCHAR,
	p_auction_no       NUMBER,
	p_auction_name     VARCHAR,
	RESULT_STATUS      OUT NUMBER
	/*
                      作者： Dwen
                      日期： 2011-07-22
                      描述： 当前拍品无代理价情况
                      RESULT_STATUS说明：
                      0: 出价成功
                      1: 用户不存在
                      2: 用户锁定冻结
                      3: 用户额度不够
                      4: 拍品已结标或过期
                      5: 加价幅度不符合
                      6: 不可重复出价
                      7: 系统锁错误
                      8: 系统末知错误
                  */
) IS
	v_now        DATE := SYSDATE;
	v_used_quota NUMBER := 0;
BEGIN
	/*===================当前拍品无代理价情况====================*/
	BEGIN
		/*====START_释放当前拍品用户的额度====*/
		/* 统计所占当前拍品的额度 */
		SELECT nvl(SUM(USE_QUOTA), 0)
			INTO v_used_quota
			FROM USER_QUOTA_USAGES
		 WHERE USER_ID = p_last_user_id
			 AND AUCTION_ID = p_auction_id
			 AND STATUS = 1;
		/* 回滚该用户当前拍品所占额度 */
		UPDATE USERS
			 SET QUOTA = QUOTA + v_used_quota
		 WHERE ID = p_last_user_id;
		/* 该用户对当前拍品额度状态更改成不占用 */
		UPDATE USER_QUOTA_USAGES
			 SET STATUS = 0
		 WHERE USER_ID = p_last_user_id
			 AND AUCTION_ID = p_auction_id;
		/*====END_释放当前拍品用户的额度====*/
	
		/* 减去用户出价所占额度 */
		UPDATE USERS SET QUOTA = QUOTA - p_price WHERE id = p_user_id;
	
		/*------当前出价等于该拍品下一个合法价------*/
		IF p_price = p_next_legal_price THEN
			/* 记录(当前)用户出价记录 */
			INSERT INTO AUCTION_PRICES
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_id,
				 p_user_nickname,
				 p_next_legal_price,
				 p_ip,
				 0,
				 v_now,
				 1,
				 p_app_id);
			INSERT INTO AUCTION_PRICES_PARTITION
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_id,
				 p_user_nickname,
				 p_next_legal_price,
				 p_ip,
				 0,
				 v_now,
				 1,
				 p_app_id);
		
			/* 更改该拍品最大价格为当前用户，最大价为下一个合法价，代理价为0*/
			UPDATE AUCTION_MAX_PRICES_NEW
				 SET USER_ID      = p_user_id,
						 nickname     = p_user_nickname,
						 MAX_PRICE    = p_next_legal_price,
						 PROXY_PRICE  = 0,
						 AUCTION_TIME = AUCTION_TIME + 1,
						 APP_ID       = p_app_id
			 WHERE AUCTION_ID = p_auction_id;
		
			/* 记录当前用户额度占用 */
			INSERT INTO USER_QUOTA_USAGES
				(ID,
				 USER_ID,
				 AUCTION_ID,
				 AUCTION_NO,
				 AUCTION_NAME,
				 USE_QUOTA,
				 STATUS,
				 USE_TYPE,
				 CREATED_AT)
			VALUES
				(USER_QUOTA_USAGES_SEQ.NEXTVAL,
				 p_user_id,
				 p_auction_id,
				 p_auction_no,
				 p_auction_name,
				 p_price,
				 1,
				 0,
				 v_now);
		
			/*------当前出价大于该拍品下一个合法价,则设为代理价------*/
		ELSE
			/* 记录(当前)用户出价记录 */
			INSERT INTO AUCTION_PRICES
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_id,
				 p_user_nickname,
				 p_next_legal_price,
				 p_ip,
				 1,
				 v_now,
				 1,
				 p_app_id);
			INSERT INTO AUCTION_PRICES_PARTITION
				(ID,
				 AUCTION_ID,
				 USER_ID,
				 nickname,
				 BID,
				 IP,
				 IS_PROXY,
				 CREATED_AT,
				 IS_MAX,
				 APP_ID)
			VALUES
				(AUCTION_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_id,
				 p_user_nickname,
				 p_next_legal_price,
				 p_ip,
				 1,
				 v_now,
				 1,
				 p_app_id);
		
			/* 更改该拍品最大价格为当前用户，最大价为下一个合法价，当前出价为代理价*/
			UPDATE AUCTION_MAX_PRICES_NEW
				 SET USER_ID      = p_user_id,
						 nickname     = p_user_nickname,
						 MAX_PRICE    = p_next_legal_price,
						 PROXY_PRICE  = p_price,
						 AUCTION_TIME = AUCTION_TIME + 1,
						 APP_ID       = p_app_id
			 WHERE AUCTION_ID = p_auction_id;
		
			/* 记录代理价出价记录 */
			INSERT INTO AUCTION_PROXY_PRICES
				(ID,
				 AUCTION_ID,
				 nickname,
				 PROXY_PRICE,
				 USER_ID,
				 CREATED_AT,
				 STATUS,
				 APP_ID)
			VALUES
				(AUCTION_PROXY_PRICES_SEQ.NEXTVAL,
				 p_auction_id,
				 p_user_nickname,
				 p_price,
				 p_user_id,
				 v_now,
				 1,
				 p_app_id);
		
			/* 记录当前用户额度占用 */
			INSERT INTO USER_QUOTA_USAGES
				(ID,
				 USER_ID,
				 AUCTION_ID,
				 AUCTION_NO,
				 AUCTION_NAME,
				 USE_QUOTA,
				 STATUS,
				 USE_TYPE,
				 CREATED_AT)
			VALUES
