				 p_user_id,
				 p_auction_id,
				 p_auction_no,
				 p_auction_name,
				 p_price,
				 1,
				 1,
				 v_now);
		END IF;
	EXCEPTION
		WHEN DUP_VAL_ON_INDEX THEN
			/*插入数据唯一索引对应的列上有重复值*/
			ROLLBACK;
			RESULT_STATUS := 8;
			RETURN;
		WHEN NO_DATA_FOUND THEN
			/* 末找到数据 */
			ROLLBACK;
			RESULT_STATUS := 8;
			RETURN;
		WHEN OTHERS THEN
			/* 其它异常 */
			ROLLBACK;
			RESULT_STATUS := 8;
			RETURN;
	END;
	RESULT_STATUS := 0;
END P_BID_NONE_PROXY;

 
