				 p_price,
				 p_ip,
				 0,
				 v_now,
				 1,
				 p_app_id);
		
			/* 更改拍品最大记录 */
			UPDATE AUCTION_MAX_PRICES_NEW
				 SET USER_ID      = p_user_id,
						 nickname     = p_user_nickname,
						 AUCTION_TIME = 1,
						 MAX_PRICE    = p_price,
						 PROXY_PRICE  = 0.0,
						 APP_ID       = p_app_id
			 WHERE AUCTION_ID = p_auction_id;
		
			/* 记录用户额度占用 */
			INSERT INTO USER_QUOTA_USAGES
				(ID,
				 USER_ID,
				 AUCTION_ID,
				 AUCTION_NO,
				 AUCTION_NAME,
				 USE_QUOTA,
				 STATUS,
				 USE_TYPE,
				 CREATED_AT)
			VALUES
