				 p_user_id,
				 p_auction_id,
				 p_auction_no,
				 p_auction_name,
				 p_price,
				 1,
				 0,
				 v_now);
		EXCEPTION
			WHEN DUP_VAL_ON_INDEX THEN
				/*插入数据唯一索引对应的列上有重复值*/
				ROLLBACK;
				RESULT_STATUS := 8;
