-- ----------------------------
-- Procedure structure for P_CUSTOMER_THAW_NO_PAY
-- ----------------------------
CREATE OR REPLACE procedure "ZHAO"."P_CUSTOMER_THAW_NO_PAY"(p_user_id        in number, --要解冻的客户编号
                                                   p_admin_id       in number, --操作人id
                                                   p_admin_name     in varchar, --操作人
                                                   p_freezeTypeName in varchar, --解冻类型
                                                   P_remarks        in varchar, --解冻理由
                                                   result_status    out number
                                                   /*
                                                                                                                                                                                                                                                                 0:成功
                                                                                                                                                                                                                                                                 1:客户号为空
                                                                                                                                                                                                                                                                 2:客户不存在或者不是不支付冻结客户
                                                                                                                                                                                                                                                                 3:其他异常
                                                                                                                                                                                                                                                                 */) is
  /*
  作者:雷美芳    update by liu.jing
  时间:2011-11-28
  描述:客户不支付解冻时竞买积分和竞买形象值归零（后台调用）
  */
  v_date      DATE := SYSDATE; --本次操作的时间
  v_user_id   NUMBER := p_user_id; --此次修改的客户ID
  v_user_name varchar(300); --当前用户

  v_buy_score_amonut  NUMBER := 0; --当前用户形象值
  v_buy_score_useable NUMBER := 0; --当前用户可用竞买积分
  v_buy_level         NUMBER;

  v_count number := 0;

  v_score_consumes_seq number; --积分消费主键

  --定义游标 查询该客户的积分情况
  cursor scores_cur is

    select c.id, c.useable_scores
      from scores c
     where c.user_id = v_user_id
       and c.score_status = 1 --可用积分
       and c.score_type != 1; -- 0：竞买积分:2：赠送积分:3：活动积分:4：委托转入 1:委托积分

begin
  if v_user_id is null then
    result_status := 1; --客户号为空
  else
    BEGIN
      select s.buy_score_amount,
             s.buy_score_useable,
             s.true_name,
             s.buy_level
        into v_buy_score_amonut,
             v_buy_score_useable,
             v_user_name,
             v_buy_level
        from users s
       where s.id = v_user_id
         and s.islock = 1 ---冻结
         and s.status = 1; ---不支付冻结
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        ROLLBACK;
        result_status := 2; --客户不存在或者不是不支付冻结客户
        return;
    END;

    /*是否是白名单用户*/
    select count(1)
      into v_count
      from user_lists
     where user_id = v_user_id
       and list_type = 0;

    if v_count > 0 then
      /*是白名单用户*/
      update users
         set islock        = 0,
             status        = 0,
             lock_operator = p_admin_name,
             lock_reason   = p_remarks,
             updated_at    = v_date
       where id = v_user_id;

    else

      /*修改客户竞买积分和形象值归零*/
      update users
         set islock            = 0,
             status            = 0,
             buy_level         = 0,
             buy_score_amount  = 0,
             buy_score_useable = 0,
             lock_operator     = p_admin_name,
             lock_reason       = p_remarks,
             updated_at        = v_date
       where id = v_user_id;

      /*用户等级变化记录*/
      insert into user_level_hists
        (id,
         user_id,
         user_type,
         change_type,
         user_change_level,
         user_changed_level,
         remark,
         op,
         op_date,
         consume_scores)
      values
        (user_level_hists_seq.nextval,
         v_user_id,
         '0', --竞买方
         '2', --降级
         v_buy_level,
         '0',
         '不支付冻结解冻后，积分形象值清零',
         p_admin_id,
         v_date,
         v_buy_score_amonut);

      update scores
         set score_status = 10, useable_scores = 0
       where score_type != 1 -- 0：竞买积分:2：赠送积分:3：活动积分:4：委托转入 1:委托积分
            --and score_status = 1 -- 可用积分
         and user_id = v_user_id;
      /*
      处理积分归零
      1.修改积分表中的类型为使用积分
      2.将该客户的积分进行消费(归零)
      3.记录积分消费流水
      */
      if v_buy_score_useable > 0 then

        select score_consumes_seq.nextval
          into v_score_consumes_seq
          from dual; --取积分消费主键
        --消费总额
        insert into score_consumes
          (id,
           user_id,
           user_name,
           score_type,
           consume_type,
           consume_scores,
           created_at,
           admin_id,
           admin_name,
           remarks)
        values
          (v_score_consumes_seq,
           v_user_id,
           v_user_name,
           '0', --积分类型：竞买
           '6', --消费类型：竞买积分清零
           v_buy_score_useable,
           v_date,
           p_admin_id,
           p_admin_name,
           p_remarks);

        --查看游标
        for s in scores_cur loop

          --消费积分明细
          insert into score_consume_details
            (id, score_id, score_consume_id, scores, created_at, remarks)
          values
            (score_consume_details_seq.nextval,
             s.id,
             v_score_consumes_seq,
             s.useable_scores,
             v_date,
             p_remarks);

        end loop;

        --积分流水信息
        insert into score_income_expenses
          (id,
           user_id,
           user_name,
           source_id,
           source_type,
           scores,
           score_type,
           direction,
           operation_type,
           created_at,
           admin_id,
           admin_name,
           remarks)
        values
          (score_income_expenses_seq.nextval,
           v_user_id,
           v_user_name,
           v_score_consumes_seq,
           'SCORE_CONSUMES',
           v_buy_score_useable,
           '0', --积分类型：竞买
           '1', --流水类型：消费
           '10', --消费：竞买积分清零
           v_date,
           p_admin_id,
           p_admin_name,
           p_remarks);

      end if;

      /*
      客户的竞买形象值归零
      1.形象流水表中记录历史,
      2.竞买形象值归零
      */
      if v_buy_score_amonut > 0 then
        --形象流水表中记录历史,
        insert into user_level_details
          (id,
           user_id,
           user_name,
           user_type,
           scores,
           direction,
           operate_type,
           resource_id,
           resource_type,
           admin_id,
           admin_name,
           remarks,
           created_at)
        values
          (user_level_details_seq.nextval,
           v_user_id,
           p_admin_name,
           '0', --竞买
           v_buy_score_amonut,
           '1', ---消费
           '7', --形象值清零
           v_user_id,
           'USERS',
           p_admin_id,
           p_admin_name,
           p_remarks,
           v_date);

      end if;

    end if;
    /*添加公共备注*/
    insert into common_remarks
      (id,
       SOURCE_ID,
       SOURCE_TYPE,
       CUSTOM_TYPE,
       CONTENT,
       ADMIN_ID,
       ADMIN_NAME,
       CREATED_AT)
    values
      (common_remarks_seq.nextval,
       v_user_id,
       'USERS',
       p_freezeTypeName,
       p_remarks,
       p_admin_id,
       p_admin_name,
       v_date);

    result_status := 0;
    return;
  end if;

exception
  WHEN OTHERS THEN
    result_status := 3; --其他异常
    return;
end p_customer_thaw_no_pay;
 
/

