         LIMIT_PAY_DATE,
         CREATED_AT,
         ORDER_STATUS,
         ORDER_NOTES,
         USER_ID,
         USER_NAME,
         BUY_TYPE,
         GET_SCORES,
         IS_GIFT,
         KEY_TIME,
         PACKAGE_ID,
         GIFT_ID,
         STOCK_PRICE,
         CONTRACT_ID)
      VALUES
