  v_a22 NUMBER := 0; --有效积分(合计)
  v_a27 NUMBER := 0; --竞买积分(合计)
  v_a32 NUMBER := 0; --委托积分(合计)

  v_a23 NUMBER := 0; --有效积分(期度增加)
  v_a28 NUMBER := 0; --竞买积分(期度增加)
