  v_a11_2 NUMBER := 0; --审核前流出保证金

  --v_a16 = v_a16_1 - v_a16_2
  v_a16_1 NUMBER := 0; -- 已支付
  v_a16_2 NUMBER := 0; --已结算

  --v_a15 = v_a15_1 - v_a15_2
  v_a15_1 NUMBER := 0; -- 已支付
  v_a15_2 NUMBER := 0; --已结算

  --v_a9 = v_a9_1 - v_f13;
  v_a9_1 NUMBER := 0;

  --v_a14 = v_a14_1 + v_f13;
  v_a14_1 NUMBER := 0;

  ---------------B  期度营业信息-------------------------
  v_b1 NUMBER := 0; --盈利合计
  v_b4 NUMBER := 0; --收益合计
  v_b7 NUMBER := 0; --抵充合计

  v_b2 NUMBER := 0; --竟拍盈利
  v_b5 NUMBER := 0; --竟拍收益
  v_b8 NUMBER := 0; --竟拍抵充

