-- ----------------------------
-- Procedure structure for P_TEST_TRANSFER
-- ----------------------------
CREATE OR REPLACE PROCEDURE "ZHAO"."P_TEST_TRANSFER"
(
	P_USER_ID            IN NUMBER,
	P_AMOUNT             IN NUMBER,
	P_BAIL_OF_PROPORTION IN NUMBER, --保证金比例
	P_DIRECTION          IN NUMBER,
	/*
                                      0: 余额转保证金
                                      1: 保证金转余额
                                      */
	RESULT_STATUS OUT NUMBER
  /*
                                      0:ok
                                      1:用户不存在
                                      2:金额不足
                                      3:其它
                                      4:用户被冻结
                                      */
) IS
  /*
    作者:李征
    日期:2012-06-02
    描述:金额转换,6.10号新版保证金额度转换比例1:50,勿删
  */
  V_ACCOUNT       NUMBER;
  V_ACCOUNT_TOTAL NUMBER;
  V_BAILS         NUMBER;
  V_USERNAME      VARCHAR(500);
  V_QUOTA         NUMBER;
  V_STATUS        VARCHAR2(255);
  NOW             DATE := SYSDATE;
  V_ACCOUNT_ID    NUMBER;
  V_MRGIN_ID      NUMBER;
BEGIN
  BEGIN
    SELECT ACCOUNT, TOTAL_ACCOUNT, BAILS, TRUE_NAME, QUOTA, STATUS
      INTO V_ACCOUNT,
           V_ACCOUNT_TOTAL,
           V_BAILS,
           V_USERNAME,
           V_QUOTA,
           V_STATUS
      FROM USERS
     WHERE ID = P_USER_ID;
  EXCEPTION
    WHEN NO_DATA_FOUND THEN
      ROLLBACK;
      RESULT_STATUS := 1;
      RETURN;
  END;
  
  /*用户冻结不能操作余额保证金*/
  IF V_STATUS != 0 THEN
    ROLLBACK;
    RESULT_STATUS := 4;
    RETURN;
  END IF;
  IF V_QUOTA IS NULL THEN
    V_QUOTA := 0;
  END IF;
RESULT_STATUS := P_DIRECTION;
return;
  /*余额转保证金*/
  IF P_DIRECTION = 0 THEN
    /*余额不足*/
    IF V_ACCOUNT IS NULL OR V_ACCOUNT < P_AMOUNT THEN
      RESULT_STATUS := 2;
      RETURN;
    END IF;

 RESULT_STATUS := 51;
 return;
    BEGIN

      /*更新用户余额,保证金信息*/
      UPDATE USERS
         SET BAILS         = BAILS + P_AMOUNT,
             ACCOUNT       = ACCOUNT - P_AMOUNT,
             TOTAL_ACCOUNT = TOTAL_ACCOUNT - P_AMOUNT,
             TOTAL_QUOTA   = TOTAL_QUOTA + P_AMOUNT * P_BAIL_OF_PROPORTION,
             QUOTA         = V_QUOTA + P_AMOUNT * P_BAIL_OF_PROPORTION
       WHERE ID = P_USER_ID;

      SELECT USER_ACCOUNTS_SEQ.NEXTVAL INTO V_ACCOUNT_ID FROM DUAL;

      SELECT USER_MARGINS_SEQ.NEXTVAL INTO V_MRGIN_ID FROM DUAL;

      /*余额流水明细*/
      INSERT INTO USER_ACCOUNTS
        (ID,
         USER_ID,
         MONEY,
         FINANCE_TYPE,
         FINANCE_DATE,
         FINANCE_ID,
         FINANCE_NAME,
         SOURCE_ID,
         SOURCE_TYPE,
         ACCOUNT_OLD,
         ACCOUNT_NEW,
         DIRECTION,
         CREATED_AT)
      VALUES
        (V_ACCOUNT_ID,
         P_USER_ID,
         P_AMOUNT,
         4,
         NOW,
         P_USER_ID,
         V_USERNAME,
         V_MRGIN_ID,
         'USER_MARGINS',
         V_ACCOUNT_TOTAL,
         V_ACCOUNT_TOTAL - P_AMOUNT,
         '1',
         NOW);

      /*保证金流水明细*/
      INSERT INTO USER_MARGINS
        (ID,
         USER_ID,
         USER_NAME,
         AMOUNT,
         MARGIN_TYPE,
         CREATED_AT,
         ADMIN_ID,
         ADMIN_NAME,
         DIRECTION,
         RESOURCE_ID,
         RESOURCE_TYPE,
         ACCOUNT_OLD,
         ACCOUNT_NEW)
      VALUES
        (V_MRGIN_ID,
         P_USER_ID,
         V_USERNAME,
         P_AMOUNT,
         '1',
         NOW,
         P_USER_ID,
         V_USERNAME,
         '0',
         V_ACCOUNT_ID,
         'USER_ACCOUNTS',
         v_bails,
         v_bails + P_AMOUNT);

      /*额度流水*/
      INSERT INTO USER_QUOTAS
        (ID,
         USER_ID,
         ADMIN_ID,
         ADMIN_NAME,
         AMOUNT,
         QUOTA_TYPE,
         OPERATE_TYPE,
         DIRECTION,
         CREATED_AT)
      VALUES
        (USER_QUOTAS_SEQ.NEXTVAL,
         P_USER_ID,
         P_USER_ID,
         V_USERNAME,
         P_AMOUNT * P_BAIL_OF_PROPORTION,
         '1',
         '0',
         '0',
         NOW);
    EXCEPTION
      WHEN OTHERS THEN
        RESULT_STATUS := 3;
        ROLLBACK;
        RETURN;
    END;
    RESULT_STATUS := 0;
    RETURN;

    /*保证金转余额*/
  ELSIF P_DIRECTION = 1 THEN
    /*保证金不足*/
    IF V_BAILS IS NULL OR V_BAILS < P_AMOUNT OR
       V_QUOTA - P_AMOUNT * P_BAIL_OF_PROPORTION < 0 THEN
      RESULT_STATUS := 2;
      RETURN;
    END IF;
    BEGIN
      /*更新用户信息*/
      UPDATE USERS
         SET BAILS         = BAILS - P_AMOUNT,
             ACCOUNT       = ACCOUNT + P_AMOUNT,
             TOTAL_ACCOUNT = TOTAL_ACCOUNT + P_AMOUNT,
             TOTAL_QUOTA   = TOTAL_QUOTA - P_AMOUNT * P_BAIL_OF_PROPORTION,
             QUOTA         = V_QUOTA - P_AMOUNT * P_BAIL_OF_PROPORTION
       WHERE ID = P_USER_ID;

      SELECT USER_ACCOUNTS_SEQ.NEXTVAL INTO V_ACCOUNT_ID FROM DUAL;

      SELECT USER_MARGINS_SEQ.NEXTVAL INTO V_MRGIN_ID FROM DUAL;

      /*用户余额流水明细*/
      INSERT INTO USER_ACCOUNTS
        (ID,
         USER_ID,
         MONEY,
         FINANCE_TYPE,
         FINANCE_DATE,
         FINANCE_ID,
         FINANCE_NAME,
         DIRECTION,
         SOURCE_ID,
         SOURCE_TYPE,
         ACCOUNT_OLD,
         ACCOUNT_NEW,
         CREATED_AT)
      VALUES
        (V_ACCOUNT_ID,
         P_USER_ID,
         P_AMOUNT,
         2,
         NOW,
         P_USER_ID,
         V_USERNAME,
         '0',
         V_MRGIN_ID,
         'USER_MARGINS',
         V_ACCOUNT_TOTAL,
         V_ACCOUNT_TOTAL + P_AMOUNT,
         NOW);

      /*用户保证金流水明细*/
      INSERT INTO USER_MARGINS
        (ID,
         USER_ID,
         USER_NAME,
         AMOUNT,
         MARGIN_TYPE,
         CREATED_AT,
         ADMIN_ID,
         ADMIN_NAME,
         DIRECTION,
         RESOURCE_ID,
         RESOURCE_TYPE,
         ACCOUNT_OLD,
         ACCOUNT_NEW)
      VALUES
        (V_MRGIN_ID,
         P_USER_ID,
         V_USERNAME,
         P_AMOUNT,
         '0',
         NOW,
         P_USER_ID,
         V_USERNAME,
         '1',
         V_ACCOUNT_ID,
         'USER_ACCOUNTS',
         v_bails,
         v_bails - P_AMOUNT);

      /*额度流水*/
      INSERT INTO USER_QUOTAS
        (ID,
         USER_ID,
         ADMIN_ID,
         ADMIN_NAME,
         AMOUNT,
         QUOTA_TYPE,
         OPERATE_TYPE,
         DIRECTION,
         CREATED_AT)
      VALUES
        (USER_QUOTAS_SEQ.NEXTVAL,
         P_USER_ID,
         P_USER_ID,
         V_USERNAME,
         P_AMOUNT * P_BAIL_OF_PROPORTION,
         '3',
         '0',
         '1',
         NOW);
    EXCEPTION
      WHEN OTHERS THEN
        RESULT_STATUS := 3;
        ROLLBACK;
        RETURN;
    END;
    RESULT_STATUS := 0;
    RETURN;
  END IF;
END P_TEST_TRANSFER;

 
