       and s.status = 0;     
      
      /*自动为交易单添加一条延期备注*/
      insert into common_remarks s
        (id,
         source_id,
         source_type,
         custom_type,
         content,
         admin_name,
         status,
         created_at)
      values
        (common_remarks_seq.nextval,
         result_delay.id,
         'USER_ORDER_DELAYS',
         '交易单自动延期',
         '交易单自动延期' || result_delay.delay_days || '天' || result_delay.delay_hours || '小时',
         '系统自动添加',
         '1',
         sysdate);
  end if;
  end loop;
end p_trade_autodelay1;

 
 
 
 
