     select sum(use_quota) into v_total_used_quota
      from user_quota_usages
     where user_id = p_user_id
       and status = 1
       and auction_id in
           (select auction_id
              from user_orders
             where user_id = p_user_id
               and status = 1
               and user_payment_id = v_payment_id);

      /*释放占用额度*/
      update user_quota_usages
