          EXIT WHEN cur_auction_fee%NOTFOUND;

          p_settlement_auction_fee(v_auction_id, v_result_status);
        END LOOP;
      END IF;

     user_payment_id := v_payment_id;
      COMMIT;
    END;
  END IF;
  RESULT_STATUS := 0;
  RETURN;
EXCEPTION
  WHEN OTHERS THEN
    RESULT_STATUS := 4;
    DBMS_OUTPUT.PUT_LINE('SQLCODE: ' || SQLCODE);
    RETURN;
end P_TRADE_PAY;


 
