-- ----------------------------
-- Procedure structure for P_USER_REFUND_4_ENGLISH
-- ----------------------------
CREATE OR REPLACE PROCEDURE "ZHAO"."P_USER_REFUND_4_ENGLISH"
(
	P_USER_ID        IN NUMBER,
	P_AMOUNT         IN NUMBER,
	P_REFUND_TYPE    IN VARCHAR,
	P_USER_BANK_ID   IN NUMBER,
	P_PAYPAL_ACCOUNT IN VARCHAR,
	P_CART_TYPE      IN VARCHAR,
	P_CART_ID        IN VARCHAR,
	P_START_AT       IN DATE,
	RESULT_STATUS    OUT NUMBER
	/*
                    创建人:李征
                    日期:2012.05.21
                    描述:英文站用户申请退款
                    RESULT_STATUS
                    0:成功
                    1:用户不存在
                    2:未身份认证
                    3:余额不足
                    4:帐户冻结,不能申请退款
                    5:退款金额不合法
                    2012-07-18 取消身份认证
                    @修改版本 v1.0
                      修改人: Dwen
                      修改日期:2013-04-01
                      修改内容:添加上门自取
                */
) IS
	V_USER_ID           NUMBER;
	V_USER_ACCOUNT      NUMBER;
	V_USERNAME          VARCHAR(255);
	V_USER_STATUS       VARCHAR2(255);
	V_USER_ID_AUTH      VARCHAR2(255);
	V_REFUND_REQUEST_ID NUMBER;
	V_REFUND_ID         NUMBER;
BEGIN
	BEGIN
		IF P_AMOUNT IS NULL OR P_AMOUNT <= 0 THEN
			RESULT_STATUS := 5;
			RETURN;
		END IF;

		SELECT U.ID, U.ACCOUNT, U.STATUS, U.TRUE_NAME, D.ID_AUTH_STATUS
			INTO V_USER_ID,
					 V_USER_ACCOUNT,
					 V_USER_STATUS,
					 V_USERNAME,
					 V_USER_ID_AUTH
			FROM USERS U
			JOIN USER_DETAILS D ON D.USER_ID = U.ID
		 WHERE ID = P_USER_ID;
	EXCEPTION
		WHEN NO_DATA_FOUND THEN
			RESULT_STATUS := 1;
			RETURN;
	END;

	--余额不足
	IF V_USER_ACCOUNT < P_AMOUNT THEN
		ROLLBACK;
		RESULT_STATUS := 3;
		RETURN;
	END IF;
	--帐户冻结,不能申请退款
	IF V_USER_STATUS != 0 AND V_USER_STATUS != 3 THEN
		ROLLBACK;
		RESULT_STATUS := 4;
		RETURN;
	END IF;

	BEGIN
		SELECT USER_REFUND_REQUESTS_SEQ.NEXTVAL INTO V_REFUND_ID FROM DUAL;
		IF P_REFUND_TYPE = 3 THEN
			--paypal
			INSERT INTO user_refund_requests
				(ID,
				 AMOUNT,
				 USER_ID,
				 USER_NAME,
				 REFUND_TYPE,
				 BANK_ACCOUNT,
				 status,
				 created_at)
			VALUES
				(USER_REFUND_REQUESTS_SEQ.NEXTVAL,
				 P_AMOUNT,
				 P_USER_ID,
				 V_USERNAME,
				 '3',
				 P_PAYPAL_ACCOUNT,
				 0,
				 SYSDATE);
		ELSIF P_REFUND_TYPE = 1 THEN
			--银行划帐 HSBC
			INSERT INTO USER_REFUND_REQUESTS
				(ID,
				 USER_ID,
				 USER_NAME,
				 AMOUNT,
				 REFUND_TYPE,
				 CURRENCY,
				 USER_BANK_ID,
				 BANK_NAME,
				 BRANCH_NAME,
				 BANK_ACCOUNT,
				 ID_CARD,
				 ID_CARD_NAME,
				 STATUS,
				 CREATED_AT)
				SELECT V_REFUND_ID,
							 P_USER_ID,
							 V_USERNAME,
							 P_AMOUNT,
							 P_REFUND_TYPE,
							 'RMB',
							 P_USER_BANK_ID,
							 UB.BANK_NAME,
							 UB.BRANCH_NAME,
							 UB.BANK_NO,
							 UB.ID_CARD,
							 UB.NAME,
							 0,
							 SYSDATE
					FROM USER_BANKS UB
				 WHERE UB.ID = P_USER_BANK_ID
					 AND UB.USER_ID = P_USER_ID
					 AND STATUS = '1';
		ELSIF P_REFUND_TYPE = 2 THEN
			SELECT USER_REFUND_REQUESTS_SEQ.NEXTVAL
				INTO V_REFUND_REQUEST_ID
				FROM DUAL;
			--上门自取
			INSERT INTO user_refund_requests
				(ID, AMOUNT, USER_ID, USER_NAME, REFUND_TYPE, status, created_at)
			VALUES
				(V_REFUND_REQUEST_ID,
				 P_AMOUNT,
				 P_USER_ID,
				 V_USERNAME,
				 '2',
				 0,
				 SYSDATE);
			INSERT INTO BOOKINGS
				(ID,
				 SOURCE_ID,
				 SOURCE_TYPE,
				 USER_NAME,
				 CARD_TYPE,
				 CARD_ID,
				 END_AT,
				 START_AT,
				 BOOKINT_AT,
				 STATUS,
				 CREATED_AT)
			VALUES
				(BOOKINGS_SEQ.NEXTVAL,
				 V_REFUND_REQUEST_ID,
				 'USER_REFUND_REQUESTS',
				 V_USERNAME,
				 P_CART_TYPE,
				 P_CART_ID,
				 P_START_AT,
				 P_START_AT,
				 P_START_AT,
				 '1',
				 SYSDATE);
		END IF;
		--更新用户余额,占用退款余额
		UPDATE USERS
			 SET ACCOUNT        = ACCOUNT - P_AMOUNT,
					 freeze_account = freeze_account + P_AMOUNT
		 WHERE ID = P_USER_ID;

		RESULT_STATUS := 0;
		RETURN;
	END;
END P_USER_REFUND_4_ENGLISH;


 
