	CURSOR C_USER_LEVEL_EXPIRED_SELL IS
		SELECT U.ID,
					 U.TRUE_NAME,
					 U.SELL_LEVEL,
					 U.SELL_SCORE_AMOUNT,
					 U.SELL_LEVEL_STARTED_AT
